import React from 'react';
import { StyleSheet, Text, View, TouchableOpacity } from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';
import { ButtonGradientProps } from './Types';

import * as Font from 'expo-font';

/*async function loadFonts() {
  await Font.loadAsync({
    'SF UI Display':require('../../../../../assets/fonts/SF-UI-Display-Semibold.ttf'),
  });
}

loadFonts();*/

const ButtonGradient = ({ onPress, containerStyle, buttonStyle, textStyle }: ButtonGradientProps) => {
  return (
    <TouchableOpacity style={[styles.container, containerStyle]} onPress={onPress}>
      <LinearGradient
        colors={['#66fcf1', '#66fcf1']}
        start={{ x: 1, y: 0 }}
        end={{ x: 0, y: 1 }}
        style={[styles.button, buttonStyle]}
      >
        <Text style={[styles.text, textStyle]}>LogIn</Text>
      </LinearGradient>
    </TouchableOpacity>
  );
};

const styles = StyleSheet.create({
  container: {
    width: 200,
    alignItems: 'center',
    marginTop: 50,
  },
  button: {
    marginTop: -20,
    height: 50,
    width: 120,
    borderRadius: 50,
    padding: 10,
    alignItems: 'center',
    justifyContent: 'center',
  },
  text: {
    //fontFamily: 'SF UI Display',
    fontWeight: 'bold',
    fontSize: 16,
    color: 'black',
  },
});

export default ButtonGradient;
